- (int)	numberPointsInPlot:(id)aPlotter
{
	int tag = (int)[aPlotter tag];
    if(tag == 0) return [[model sbcLink] cbTestCount];
	else		 return 1000;
}
   
- (void) plotter:(id)aPlotter index:(uint32_t)index x:(double*)xValue y:(double*)yValue
{
	int tag = (int)[aPlotter tag];
    if(tag == 0){
		if(index>100){
			*xValue = 0;
			*yValue = 0;
		}
		else {
			NSPoint track = [[model sbcLink] cbPoint:index];
			*xValue = track.x;
			*yValue = track.y;
		}
	}
	else {
		*yValue = [[model sbcLink] recordSizeHisto:(int)index];
		*xValue = index;
	}
}

- (BOOL) plotter:(id)aPlotter crossHairX:(double*)xValue crossHairY:(double*)yValue
{
	if([aPlotter tag] == 0){
		if([[model sbcLink] productionSpeedValueValid])	{
			*xValue = [[model sbcLink] payloadSize]/1000.;
			*yValue = [[model sbcLink] productionSpeed];
			return YES;
		}
	}
	return NO;
}

