- (void) updateTank:(int)index
{
	switch(index){
		case 0: [monitor0 setNeedsDisplay:YES]; break;
		case 1: [monitor1 setNeedsDisplay:YES]; break;
		case 2: [monitor2 setNeedsDisplay:YES]; break;
		case 3: [monitor3 setNeedsDisplay:YES]; break;
	}
}

