@implementation ORSerialPortModel

- (id) init
{
	self = [super init];
    [self registerNotificationObservers];
	return self;
}

- (void) dealloc
{
    [[NSNotificationCenter defaultCenter] removeObserver:self];
    [portName release];
    [serialPort release];
	[super dealloc];
}

- (void) registerNotificationObservers
{
	NSNotificationCenter* notifyCenter = [NSNotificationCenter defaultCenter];
	[notifyCenter removeObserver:self];
    
    [notifyCenter addObserver : self
                     selector : @selector(dataReceived:)
                         name : ORSerialPortDataReceived
                       object : nil];
    
    [notifyCenter addObserver : self
                     selector : @selector(portsChanged:)
                         name : ORSerialPortListChanged
                       object : nil];
}

#pragma mark •••Accessors
- (BOOL) portWasOpen
{
    return portWasOpen;
}

- (void) setPortWasOpen:(BOOL)aPortWasOpen
{
    portWasOpen = aPortWasOpen;
}

- (NSString*) portName
{
    if(portName==nil)return @"";
    else return portName;
}

- (void) setPortName:(NSString*)aPortName
{
    [[[self undoManager] prepareWithInvocationTarget:self] setPortName:portName];
    if(aPortName==nil)aPortName = @"";

    if(![aPortName isEqualToString:portName]){
        [portName autorelease];
        portName = [aPortName copy];
    }
    
    [self openPortIfNeeded];
    
    [[NSNotificationCenter defaultCenter] postNotificationName:ORSerialPortModelPortNameChanged object:self];
}

- (void) portsChanged:(NSNotification*)aNote
{
    NSArray* portList = [[ORSerialPortList sharedSerialPortList] portList];
    BOOL portIsGone = YES;
    for ( ORSerialPort* aPort in portList) {
        if([portName isEqualToString:[aPort name]]){
            portIsGone = NO;
            break;
        }
    }
    if(portIsGone){
        [[self undoManager] disableUndoRegistration];
        [self setPortName:nil];
        portWasOpen = NO;
        [self setSerialPort:nil];
        [[self undoManager] enableUndoRegistration];
   }
}

- (void) openPortIfNeeded
{
    BOOL valid = NO;
    NSArray* portList = [[ORSerialPortList sharedSerialPortList] portList];
    for ( ORSerialPort* aPort in portList) {
        if([portName isEqualToString:[aPort name]]){
            [self setSerialPort:aPort];
            if(portWasOpen){
                [self openPort:YES];
            }
            valid = YES;
            break;
        }
    }
    if(!valid){
        [self setSerialPort:nil];
    }
}

- (ORSerialPort*) serialPort
{
    return serialPort;
}

- (void) setSerialPort:(ORSerialPort*)aSerialPort
{
    [aSerialPort retain];
    [serialPort release];
    serialPort = aSerialPort;

    [[NSNotificationCenter defaultCenter] postNotificationName:ORSerialPortModelSerialPortChanged object:self];
}


